import axios from 'axios'
import { useState, useEffect } from 'react'
import { Link, useHistory } from 'react-router-dom'
import { useNavigate } from 'react-router'
import { URL } from '../../config'
import AdminNav from '../../Components/Navbar/adminNav'
import CourseChapter from './courseChapter'
import { toast } from 'react-toastify'
import'./addCourseChapter.css'

const AddChapter = () => {
    const [courseChapterid, setCourseChapterId] = useState('')
    const [chapterTitle, setChapterTitle] = useState('')   
    const [numOfVideo, setNumOfVideo] = useState('')
    const [courseid, setCourseid] = useState('')

    const navigate = useNavigate()
  
    const addChaptertoDb = () =>{
          //const artistId = 3         

         if(numOfVideo.length == 0){
             toast.warning('please enter title')
         } else if (chapterTitle.length == 0){
             toast.warning('please enter thubnail')
         }else{
              const body = {
                courseChapterid,
                chapterTitle,
                numOfVideo,
                courseid,
              }

              const url = `${URL}/chapter`

              axios.post(url, body).then((response) => {

                 // get the server result
                  const result = response.data
                  if (result['status'] == 'success'){
                    toast.success('added new album')
                     navigate('/admin')
                  }else {
                    toast.error(result['error'])
                  }
              })
         }

    }

    return (

      <>
   <Navibar/>
    {/* {dataAdded && <h3 class="alert alartbox alert-primary" role="alert">{dataAdded}</h3>} */}

    <div className="wrapper">
      <div className="app-wrapper">
        <div>
          <h2 className="title">Add Chpater</h2>
        </div>
        <br />
        <form className="form-wrapper">
          <div className="row">
            <div className="col">
              <div className="name">
                <label className="label">Chapter Name</label>
                <input
                  className="input"
                  type="text"
                  name='chapterTitle'
                  value={chapterTitle}
                  placeholder="Chapter_Name"
                  onChange={(e)=>setChapterTitle(e.target.value)}
                />
                {errors.chapterTitle &&  <p className="error">{errors.chapterTitle}</p>}
              </div>
            </div>
           
          <div className="row">
            <div className="col">
              <div className="name">
                <label className="label">Videos</label>
                <input
                  className="input"
                  type="text"
                  name='numOfVideo'
                  value={NumOfVideo}
                  placeholder="Vedios No."
                  onChange={(e)=>setNumOfVideo(e.target.value)}
                />
                 {errors.numofChapter && ( 
                 <p className="error">{errors.numofChapter}</p> 
                 )} 
              </div>
            </div>
            <div className="col">
              <div className="name">
                <label className="label">User_Id</label>
                <input
                  className="input"
                  type="number"
                  name='user'
                  value={user_id}
                  placeholder="User Id"
                  onChange={(e)=>setUser_id(e.target.value)}
                />
                {errors.user_id && <p className="error">{errors.user_id}</p>}
              </div>
            </div>
          </div>
          <div className="col">
              <div className="name">
                <label className="label">Description</label>
                <textarea
                  className="input"
                  type="text"
                  name='courseBrief'
                  value={courseBrief}
                  placeholder= "50+ Introduction"
                  onChange={(e)=>setCourseBrief(e.target.value)}
                />
                {errors.courseBrief && <p className="error">{errors.courseBrief}</p>}
              </div>
            </div>
          </div>
                   <div>
            <button className='submit' onClick={addCourseToDB}>Add Course</button>
          </div>
        </form>
      </div>
    </div>
  </>


   
    )
}

export default AddChapter

