import axios from 'axios'
import { useState, useEffect } from 'react'
import { Link, useHistory } from 'react-router-dom'
import { useNavigate } from 'react-router'
import { URL } from '../../config'
import AdminNav from '../../Components/Navbar/adminNav'
import Admin from './admin'
import { toast } from 'react-toastify'

import Navibar from '../../Components/Navbar/Navibar';
// courseid
const AddCourses = () => {
  // const [courseid, setCourseid] = useState('')
  const [courseTitle, setCourseTitle] = useState('')
  const [courseBrief, setCourseBrief] = useState('')
  const [numofChapter, setNumofChapter] = useState('')
  const [user_id, setUser_id] = useState('')

  const [errors, setErrors] = useState({});
  const [values , setValues] = useState({});

  const course = {courseTitle,courseBrief,numofChapter,user_id}

  const navigate = useNavigate()



  const addCourseToDB = () => {

    event.preventDefault();
    setErrors(validation(user))
    //  const courseid = 6

    if (courseTitle.length == 0) {
      toast.warning('please enter title')
    } else if (courseBrief.length == 0) {
      toast.warning('please enter thubnail')
    } else {
      const body = {
        courseTitle,
        courseBrief,
        numofChapter,
        user_id
      }

      const url = `${URL}/courses`
      console.log(body)
      axios.post(url, body).then((response) => {

        // get the server result
        const result = response.data
        if (result['status'] == 'success') {
          toast.success('added new album')
          navigate('/admin')
        } else {
          toast.error(result['error'])
        }
      })
    }

  }

  return (
    <>
   <Navibar/>
    {/* {dataAdded && <h3 class="alert alartbox alert-primary" role="alert">{dataAdded}</h3>} */}

    <div className="wrapper">
      <div className="app-wrapper">
        <div>
          <h2 className="title">Add Course</h2>
        </div>
        <br />
        <form className="form-wrapper">
          <div className="row">
            <div className="col">
              <div className="name">
                <label className="label">Course_Title</label>
                <input
                  className="input"
                  type="text"
                  name='courseTitle'
                  value={courseTitle}
                  onChange={(e)=>setCourseTitle(e.target.value)}
                />
                {errors.courseTitle &&  <p className="error">{errors.courseTitle}</p>}
              </div>
            </div>
            <div className="col">
              <div className="name">
                <label className="label">Description</label>
                <input
                  className="input"
                  type="textBox"
                  name='courseBrief'
                  value={courseBrief}
                  onChange={(e)=>setCourseBrief(e.target.value)}
                />
                {errors.courseBrief && <p className="error">{errors.courseBrief}</p>}
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col">
              <div className="name">
                <label className="label">Chapter Count</label>
                <input
                  className="input"
                  type="text"
                                   name='numOfChapter'
                  value={numofChapter}
                  onChange={(e)=>setNumofChapter(e.target.value)}
                />
                 {errors.numofChapter && ( 
                 <p className="error">{errors.numofChapter}</p> 
                 )} 
              </div>
            </div>
            <div className="col">
              <div className="name">
                <label className="label">User_Id</label>
                <input
                  className="input"
                  type="number"
                  name='user'
                  value={user_id}
                  onChange={(e)=>setUser_id(e.target.value)}
                />
                {errors.user_id && <p className="error">{errors.user_id}</p>}
              </div>
            </div>
          </div>
                   <div>
            <button className='submit' onClick={addCourseToDB}>Add Course</button>
          </div>
        </form>
      </div>
    </div>
  </>

    
  )
}

export default AddCourses

