import { useState } from 'react'
import { Link } from 'react-router-dom'
import './index.css'
import { toast } from 'react-toastify'
import axios from 'axios'
import { useNavigate } from 'react-router'
import { URL } from '../../config'
import Navibar from '../../Components/Navbar/Navibar'

const ForgotPassword = () => {
  const [email, setEmail] = useState('')
  const [newPassword, setNewPassword] = useState('')
  const [confirmNewPassword, setConfirmNewPassword] = useState('')

  const navigate = useNavigate()

  const forgotpassword = () => {
    // if (newPassword.length == 0) {
    //   toast.warning('please enter your New Password')
    // } else if (confirmNewPassword.length == 0) {
    //     toast.warning('please enter Confirm New Password')
    // }  else
     {
      const body = {
        email,
        newPassword,
        confirmNewPassword,
      }

      // url to make signin api call
      const url = `${URL}/user/forgotpassword/${email}`

      // make api call using axios
      axios.post(url, body).then((response) => {
        // get the server result
        const result = response.data
        console.log(result)
        if (result['status'] == 'success') {
          toast.success('Welcome to the application')

          // get the data sent by server
          const { id, firstName, lastName } = result['data']

          // persist the logged in user's information for future use
          sessionStorage['id'] = id
          sessionStorage['firstName'] = firstName
          sessionStorage['lastName'] = lastName

          // navigate to home component
          navigate('/')
        } else {
          toast.error('Invalid user name or password')
        }
      })
    }
  }

  return (


    <>
    {/* {dataAdded && <h3 class="alert alartbox alert-primary" role="alert">{dataAdded}</h3> */}
    }
   <div className='login-wrapper'>
       <div className='login-app-wrapper'>
           <div>
               <h2 className='title'>Share your registered email</h2>
               <span className="myspan">You will get a mail regarding your password</span>
           </div>
           <br/>
           <form className='form-wrapper'>
               <div className='email'>
                   <label className='label'>Email</label>
                   <input className='input' type='email' name='userEmail' onChange={(e) => setUserEmail(e.target.value)} required/>
                   
               </div>
               <div>
           <button className="submit" onClick={handleSubmit}>
             Share
           </button>
         </div>
               <div>
               <Link to="/signin">Login</Link>
               </div>
           </form>
       </div>
   </div>
   </>
  )
    
}

export default ForgotPassword

