import React, { useEffect, useState } from "react";
import { Navbar, Nav, Container } from "react-bootstrap";
import { BrowserRouter, Routes, Route, Link, NavLink } from "react-router-dom";
import logo from "../image/logo.png"

const Navigation = () => {
  const myuserobj = localStorage.getItem("user");
  const user = JSON.parse(myuserobj);

  const [userobj, setUserobj] = useState(false);

  useEffect(() => {
    const loggedIn = localStorage.getItem("user");
    if (loggedIn) {
      setUserobj(true);
    }
  }, []);

  const logout = () => {
    localStorage.removeItem("user");
    setUserobj(false);
  };
  return (
    <div>
      <BrowserRouter>
        <div className="App">
          <Navbar className="mynavbar">
            <Container>
              <Navbar.Brand href="/">
                <img
                  src={logo}
                  className="d-inline-block align-top logo"
                  alt="psd logo"
                />
              </Navbar.Brand>
              <Nav className="me-auto">
                <NavLink className="nav-link" to="/">
                  Home
                </NavLink>
                <NavLink className="nav-link" to="/Aboutus">
                  About Us
                </NavLink>
                <NavLink className="nav-link" to="/Contact">
                  Contact Us
                </NavLink>
               
              </Nav>
              <Nav>
               
                {!userobj ? (
                  <Nav.Link as={Link} to="/signup">
                    Register
                  </Nav.Link>
                ) : (
                  ""
                )}
                {!userobj ? (
                  <Nav.Link as={Link} to="/api/psd/user">
                    Login
                  </Nav.Link>
                ) : (
                  ""
                )}
                {userobj ? (
                  <Nav.Link as={Link} to="/" onClick={logout}>
                    Logout
                  </Nav.Link>
                ) : (
                  ""
                )}
              </Nav>
            </Container>
          </Navbar>
          <div>
            <Routes>
            
                <Route
                  path="/api/psd/register"
                  element={<Register />}
                />
                <Route path="/api/psd/user" element={<Login />} />
                {userobj && (user.role=="ADMIN" | user.role=="CUSTOMER")?<Route
                  path="/api/psd/user/Profile"
                  element={<UserHome />}
                />:<Route
                path="/api/psd/user/Profile"
                element={<Login />}
              />}
                <Route path="/bookservice" element={<BookServices />} />
                <Route path="/feedback" element={<FeedbackForm />} />
                <Route
                  path="/api/psd/employee"
                  element={<RegisterEmp />}
                />
                <Route
                  path="/forgetpassword"
                  element={<ForgetPassword />}
                />
                  </Routes>
          </div>
          <Footer />  
        </div>
      </BrowserRouter>
    </div>
  );
};

export default Navigation;
