import { useState } from 'react';
import AddCourses from '../Admin/addCourses';
import AddChapter from '../CourseChapter/addCourseChapter';
import AddContent from '../CourseChapterContent/addContent';
import Courses from '../courses/courses';
import UpdateProfile from '../UpdateProfile';
import User from './user';
import './UserHome.css'


function UserHome() {
    //const user = useSelector((state) => state.user.user)
    const userobj=localStorage.getItem('user');
    const user=JSON.parse(userobj);
    const [rightComponent,setRightComponent] = useState("")

if(user.role==="STUDENT")
{
    return (
        <div className="row">
            <div className="col-3 side-panel p-3">
                 <nav className="mynav flex-column" >
                    <span className="nav-link mb-3"  onClick={() => {setRightComponent(<User/>)}}>My Profile</span>
                    <hr className='myline'/>
                    <span className="nav-link mb-3" onClick={() => {setRightComponent(<UpdateProfile/>)}}>Update Profile</span>
                    <hr className='myline'/>
                    <span className="nav-link mb-3" onClick={() => {setRightComponent(<Courses/>)}}>My Orders</span>
                    {/* <hr className='myline'/>
                    <span className="nav-link mb-3" onClick={() => {setRightComponent(<BookServices/>)}}>Book Service</span> */}
                </nav> 
            </div>
            <div className="col-9 box-height">
                    {rightComponent}
            </div>
        </div>
    )
}
if(user.role==="ADMIN")
{
    return (
        <div className="row">
            <div className="col-3 side-panel p-3">
                <nav className="mynav flex-column">
                    <span className="nav-link mb-3 heading" onClick={() => {setRightComponent(<User/>)}}>My Profile</span>
                    <hr className='myline'/>
                    <span className="nav-link mb-3" onClick={() => {setRightComponent(<UpdateProfile/>)}}>Update Profile</span>
                    <hr className='myline'/>
                    <span className="nav-link mb-3 heading" onClick={() => {setRightComponent(<AddCourses/>)}}>Unverified Employees</span>
                    <hr className='myline'/>
                    <span className="nav-link mb-3 heading" onClick={() => {setRightComponent(<AddChapter/>)}}>Verified Employees</span>
                    <hr className='myline'/>
                    <span className="nav-link mb-3 heading" onClick={() => {setRightComponent(<AddContent/>)}}>Services</span>
                    <hr className='myline'/>
                    <span className="nav-link mb-3 heading" onClick={() => {setRightComponent(<User/>)}}>All customers</span>

                </nav>


            </div>
            <div className="col-9 box-height">
                    {rightComponent}
            </div>
        </div>
    )
}
}
export default UserHome;
