import Image from 'react-bootstrap/Image';
// import image from 'D:/SigninSigupFormats/SigninSigupFormat/my-app/src/Pages/images/eler.jpg';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react'
import { Link } from 'react-router-dom'
import './index.css'
import { toast } from 'react-toastify'
import axios from 'axios'
import { useNavigate } from 'react-router'
import { URL } from '../../config'
import './index.css'
import Navibar from '../../Components/Navbar/Navibar';

const Signin = () => {
  const [user_id, setUserId] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const[role, setRole]= useState('')

  const navigate = useNavigate()

  const signinUser = () => {
    if (email.length == 0) {
      toast.warning('please enter your Email')
    } else if (password.length == 0) {
      toast.warning('please enter your Password')
    } else {
      const body = {
        email,
        password,
        role,
      }

      // url to make signin api call
      const url = `${URL}/user/signin`

      // make api call using axios
      axios.post(url, body).then((response) => {
        // get the server result
        const result = response.data
        console.log(result)
        if (result['status'] == 'success') {
          toast.success('Welcome to the application')

          // get the data sent by server
          const { user_id, fName, lName, role } = result['data']

          // persist the logged in user's information for future use
          sessionStorage['id'] = user_id
          sessionStorage['fName'] = fName
          sessionStorage['lName'] = lName
          sessionStorage['loginStatus'] = 1
          
          
          // navigate to home component
          if(role === 'user'){
            console.log('usercourses')
            navigate('/usercourses')
          }else if(role === 'admin'){
            navigate('/admin')
          }

        } else {
          toast.error('Invalid user name or password')
        }
      })
    }
  }
  return (
    <>
   {/* {dataAdded && <h3 class="alert alartbox alert-primary" role="alert">{dataAdded}</h3>} */}
  <div className='login-wrapper'>
      <div className='login-app-wrapper'>
          <div>
              <h2 className='title'>Log in</h2>
          </div>
          <form className='form-wrapper'>
              <div className='email'>
                  <label className='label'>Email</label>
                  <input className='input' type='text' name='userEmail' value={values.userEmail} onChange={handleChange}/>
                  {/* {errors.userEmail && <p className='error'>{errors.userEmail}</p>} */}
              </div>
              <div className='name'>
                  <label className='label'>Password</label>
                  <input className='input' type='password' name='password' onChange={(e) => {
                  setPassword(e.target.value)
                }}/>
                  {/* {errors.password && <p className='error'>{errors.password}</p>} */}
              </div>
              <div>
                  <button className='login-button' onClick={signinUser}>Log in</button>
              </div>
              <div className="form-group">
                  <Link to='/forgetpassword'>forget password?</Link>
              </div>
              <div className="form-group">
                  New User? Register here <Link to='/signup'>Register Now</Link>
              </div>
              
              <div>
              <Link to="/">Home</Link>
              </div>
          </form>
      </div>
  </div>
  </>
)
  return (
    
    <div  class="signin">
      <Navibar/>
      
      <h1>Signin</h1>
      <br></br>
     
      <div className="row">
        <div className="col"></div>
        <div className="col">
          <div className="form shadow">
            <div className="mb-3">
              <label htmlFor="" className="label-control">
                Email address
              </label>
              <input
                onChange={(e) => {
                  setEmail(e.target.value)
                }}
                type="text"
                className="form-control"
              />
            </div>

            <div className="mb-3">
              <label htmlFor="" className="label-control">
                Password
              </label>
              <input
                onChange={(e) => {
                  setPassword(e.target.value)
                }}
                type="password"
                className="form-control"
              />
            </div>

            <div className="mb-3">
              <div>
                No account yet? <Link to="/signup">Signup here</Link>
              </div>
              <br></br>
              <div>
                <Link to="/forgotpassword">forgot password? </Link>
              </div>
              <br></br>
              <div>
                <Link to="/">Home Page</Link>
              </div>
              <br></br>
              <button onClick={signinUser} className="btn btn-primary">
                Signin
              </button>
            </div>
          </div>
        </div>
        <div className="col"></div>
      </div>
    </div>
  )
}

export default Signin
