import { useNavigate } from "react-router";
import { useEffect, useState } from "react";
import axios from "axios";
import { URL } from "../../config";
import AdminNav from "../../Components/Navbar/adminNav";
import CourseChapterContentRow from "../../Components/coursechaptercontentRow";
import { toast } from "react-toastify";
import { Link } from "react-router-dom";
import "./coursechaptercontent.css";

const CourseChapterContent = () => {
  const navigate = useNavigate();

  const [coursechaptercontent, setContent] = useState([]);

  const getContent = () => {
    const url = `${URL}/content/all`;

    axios.get(url).then((response) => {
      const result = response.data;
      console.log(result);
      if (result["status"] == "success") {
        setContent(result["data"]);
      } else {
        toast.error(result["error"]);
      }
    });
  };

  useEffect(() => {
    getContent();
    console.log("getting called");
  }, []);

  return (
    <div className="col">
      <div>
        <AdminNav />
      </div>
      <div>
        <h2 class="title">Content</h2>

        <Link to="/addContent">
          <a className="btn btn-success">Add Content</a>
        </Link>
      </div>
      <br></br>
      <br></br>
      <div>
      <table class="table">
  <thead class="thead-dark">
    <tr>
      <th scope="col">#</th>
      <th scope="col">First</th>
      <th scope="col">Last</th>
      <th scope="col">Handle</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td>Larry</td>
      <td>the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</table>

<table class="table">
  <thead class="thead-light">
    <tr>
    <th className="table-primary">contentid</th>
              <th className="table-primary">timeRequiredToCompleteTheCourse</th>
              <th className="table-primary">chapterContent</th>
              <th className="table-primary">courseChapterid</th>
              <th className="table-primary">Link</th>
    </tr>
  </thead>
  <tbody>
            {coursechaptercontent.map((item) => {
              return <CourseChapterContentRow coursechaptercontent={item} />;
            })}
          </tbody>
  
</table>
        {/* <table className="table table-striped">
          <thead>
            <tr>
              <th className="table-primary">contentid</th>
              <th className="table-primary">timeRequiredToCompleteTheCourse</th>
              <th className="table-primary">chapterContent</th>
              <th className="table-primary">courseChapterid</th>
              <th className="table-primary">Link</th>

              <th className="table-primary">Action</th>
            </tr>
          </thead>
          
        </table> */}
      </div>
    </div>
  );
};

export default CourseChapterContent;
