import { useEffect, useState } from 'react'
import './index.css'
import { toast } from 'react-toastify'
import axios from 'axios'
import { useNavigate } from 'react-router'
import { URL } from '../../config'
import AfterLogNavibar from '../../Components/Navbar/AfterLogNavibar'

const UpdateProfile = () => {

  const [User_id, setUser_Id] = useState(sessionStorage.getItem('id'));
  const [fName, setFirstName] = useState("");
  const [lName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [qualification, setQualification] = useState("");
  const [contact, setContact] = useState("");
  const [adharNo, setAdharNo] = useState("");
  const [data, setData] = useState([]);


  const [errors, setErrors] = useState('')

  const navigate = useNavigate()

  const updateprofile = () => {
    {
      const body = {
        fName,
        lName,
        email,
        qualification,
        contact,
        adharNo,
      }

      // url to make updateprofile api call
      const url = `${URL}/user/updateprofile`

      // make api call using axios
      axios.put(url, body).then((response) => {
        // get the server result
        const result = response.data
        console.log(result)
        if (result['status'] == 'success') {
          toast.success('Profile Updated')

          // get the data sent by server
          const { email, contact } = result['data']

          // persist the logged in user's information for future use
          sessionStorage['email'] = email
          sessionStorage['contact'] = contact

          // navigate to profile component
          navigate('/profile')
        } else {
          toast.error('Data not get updated')
        }
      })
    }
  }
  const getData = () => {
    axios.get(`${URL}/user/find/${User_id}`).then((response) => {
      const result = response.data;
      setData(response.data);
      setFirstName(result.data.fName);
      setLastName(result.data.lName);
      setEmail(result.data.email);
      setQualification(result.data.qualification);
      setContact(result.data.contact);
      setAdharNo(result.data.adharNo);
    });
  }

  useEffect(() => {
    getData();
  }, []);

  return (

    <>
      <AfterLogNavibar />
      {/* {dataAdded && <h3 class="alert alartbox alert-primary" role="alert">{dataAdded}</h3>} */}

      <div className="wrapper">
        <div className="app-wrapper">
          <div>
            <h2 className="title">Register Your Details</h2>
          </div>
          <br />
          <form className="form-wrapper">
            <div className="row">
              <div className="col">
                <div className="name">
                  <label className="label">First Name</label>
                  <input
                    className="input"
                    type="text"
                    name='fname'
                    value={fName}
                    onChange={(e) => setFirstName(e.target.value)}
                  />
                  {errors.fName && <p className="error">{errors.fName}</p>}
                </div>
              </div>
              <div className="col">
                <div className="name">
                  <label className="label">Last Name</label>
                  <input
                    className="input"
                    type="text"
                    name='lname'
                    value={lName}
                    onChange={(e) => setLastName(e.target.value)}
                  />
                  {errors.lName && <p className="error">{errors.lName}</p>}
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col">
                <div className="name">
                  <label className="label">Contact Number</label>
                  <input
                    className="input"
                    type="number"
                    min='7000000000'
                    max='9999999999'
                    name='contact'
                    value={contact}
                    onChange={(e) => setContact(e.target.value)}
                  />
                  {errors.contact && (
                    <p className="error">{errors.contact}</p>
                  )}
                </div>
              </div>
              <div className="col">
                <div className="name">
                  <label className="label">Email</label>
                  <input
                    className="input"
                    type="email"
                    name='email'
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                  {errors.email && <p className="error">{errors.email}</p>}
                </div>
              </div>
            </div>
            <div className="row">
             
              <div className="col">
                <div className="name">
                  <label className="label">Qualification</label>
                  <input
                    className="input"
                    type="text"
                    name='qualification'
                    value={qualification}
                    onChange={(e) => setQualification(e.target.value)}
                  />
                  {errors.qualification && <p className="error">{errors.qualification}</p>}
                </div>
              </div>
            </div>

            <div>
              <button className='submit' onClick={signupUser}>Register</button>
            </div>
          </form>
        </div>
      </div>
    </>

  )
}

export default UpdateProfile

