import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom'
import { useState } from 'react'
import { toast } from 'react-toastify'
import axios from 'axios'
import { useNavigate } from 'react-router'
import { URL } from '../../config'
import Navibar from '../../Components/Navbar/Navibar';
import './index.css'
import { isDate } from 'moment-timezone';
import { Button } from 'react-bootstrap';
import "../Signup/style.css"
import validation from './validation';

const Signup = () => {
  const [fName, setFirstName] = useState('')
  const [lName, setLastName] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [registrationDate, setRegistrationDate] = useState(new Date())
  const [qualification, setQualification] = useState('')
  const [publishedCourses, setnum_Of_Published_Courses] = useState('4')
  const [contact, setContact] = useState('')
  const [adharNo, setAdharNo] = useState('')
  const [role, setRole] = useState('STUDENT')
  const [errors, setErrors] = useState({});


  // used to navigate from one component to another
  const navigate = useNavigate()

  const signupUser = (event) => {
    event.preventDefault();
    setErrors(validation(event.target.value))
    // if (fName.length == 0) {
    //   toast.warning('Please enter First Name')
    // } else if (lName.length == 0) {
    //   toast.warning('Please enter Last Name')
    // } else if (email.length == 0) {
    //   toast.warning('Please enter Email')
    // } else if (password.length == 0) {
    //   toast.warning('Please enter Password')
    //   // } else if (registrationDate.length == 0) {
    //   //   toast.warning('Please enter your registrationDate')
    // } else if (qualification.length == 0) {
    //   toast.warning('Please enter your Qualification')
    // } else if (publishedCourses.length == 0) {
    //   toast.warning('Please enter your num_Of_Published_Courses')
    // } else if (contact.length == 0) {
    //   toast.warning('Please enter your Secret Question')
    // } else if (contact.length > 10) {
    //   toast.error('You Entering More Than 10 Digits ContactNo')
    // } else if (contact.length < 10) {
    //   toast.error('You Entering less Than 10 Digits ContactNo')
    // }
    //  else if (adharNo.length == 0) {
    //   toast.warning('Please enter your adharNo')  
    // } else if (adharNo.length >10) {
    //   toast.error('You Entering More Than 10 Digits AdharNo')  
    // }else if (adharNo.length < 10) {
    //   toast.error('You Entering less Than 10 Digits AdharNo')  
    // }

    //  else if (password != confirmPassword) {
    //   toast.warning('Password does not match')
    //   } 
    // else
     {
      const body = {
        fName,
        lName,
        email,
        password,
        registrationDate,
        qualification,
        publishedCourses,
        contact,
        adharNo,
        role,
      }

      // url to call the api
      const url = `${URL}/user/signup`

      // http method: post
      // body: contains the data to be sent to the API
      axios.post(url, body).then((response) => {
        // get the data from the response
        const result = response.data
        console.log(result)
        if (result['status'] == 'success') {
          toast.success('Successfully signed up new user')

          // navigate to the signin page
          navigate('/signin')
        } else {
          toast.error(result['error'])
        }
      })
    }
  }

  // let today = new Date().toISOString().slice(0, 10)

  // console.log(today)


  return (
    <>
      <Navibar />
      {/* {dataAdded && <h3 class="alert alartbox alert-primary" role="alert">{dataAdded}</h3>} */}

      <div className="wrapper">
        <div className="app-wrapper">
          <div>
            <h2 className="title">Register Your Details</h2>
          </div>
          <br />
          <form className="form-wrapper">
            <div className="row">
              <div className="col">
                <div className="name">
                  <label className="label">First Name</label>
                  <input
                    className="input"
                    type="text"
                    onChange={(e) => {
                      setFirstName(e.target.value)
                    }}
                  />
                  {errors.fName &&  <p className="error">{errors.fName}</p>}
                </div>
              </div>
              <div className="col">
                <div className="name">
                  <label className="label">Last Name</label>
                  <input
                    className="input"
                    type="text"
                    onChange={(e) => {
                      setLastName(e.target.value)
                    }}
                  />
                  {errors.lName && <p className="error">{errors.lName}</p>}
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col">
                <div className="name">
                  <label className="label">Contact Number</label>
                  <input
                    className="input"
                    type="number"
                    min='7000000000'
                    max='9999999999'
                    onChange={(e) => {
                      setContact(e.target.value)
                    }}
                  />
                   {errors.contact && ( 
                   <p className="error">{errors.contact}</p> 
                   )} 
                </div>
              </div>
              <div className="col">
                <div className="name">
                  <label className="label">Email</label>
                  <input
                    className="input"
                    type="text"
                    onChange={(e) => {
                      setEmail(e.target.value)
                    }}
                  />
                  {errors.email && <p className="error">{errors.email}</p>}
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col">
                <div className="name">
                  <label className="label">Password</label>
                  <input
                    className="input"
                    type="password"
                    onChange={(e) => {
                      setPassword(e.target.value)
                    }}
                  />
                  {errors.password && <p className="error">{errors.password}</p>}
                </div>
              </div>
              <div className="col">
                <div className="name">
                  <label className="label">Qualification</label>
                  <input
                    className="input"
                    type="text"
                    onChange={(e) => {
                      setQualification(e.target.value)
                    }}
                  />
                  {errors.qualification && <p className="error">{errors.qualification}</p>}
                </div>
              </div>
            </div>

            <div>
              <button className='submit' onClick={signupUser}>Register</button>
            </div>
          </form>
        </div>
      </div>
    </>
  );
}

