const validation = (values) => {

    let errors = {};

    if(!values.chapterTitle){
        errors.chapterTitle = "Chapter Name is Required."
    }

    if(!values.numOfVideo){
        errors.courseBrief = "Brief Introduction is Required."
    }

    if(!values.numofChapter){
        errors.numofChapter = "Chapter Count is Required."
    }

    if(!values.user_id){
        errors.user_id = "User Id  is Required."
    }

  return errors;
}
export default validation;
